package com.oscgc.securevideo.server.service.impl;import java.util.ArrayList;import java.util.List;import com.google.common.base.Preconditions;import com.google.common.collect.Lists;import org.springframework.stereotype.Service;import com.oscgc.securevideo.server.model.Video;import com.oscgc.securevideo.server.service.VideoService;/** * Created by Yq on 2015/5/28. */@Servicepublic class VideoServiceImpl implements VideoService {        private static List<Video> instance = new ArrayList<Video>();        public static List<Video> getInstance() {        return instance;    }        @Override    public List<Video> findAllVideos() {        return instance;    }    @Override    public List<Video> findByTenant(String tenantId) {        Preconditions.checkArgument(tenantId != null);        List<Video> videos = Lists.newArrayList();        for (Video video : instance) {            if (tenantId.equals(video.getTenantId())) {                videos.add(video);            }        }        return videos;    }    @Override    public Video findVideoById(String id) {        List<Video> videos = instance;        Video video = null;        if (videos != null && videos.size() > 0) {            for (int i = 0; i < videos.size(); i++) {                video = videos.get(i);                if (video.getId().equals(id)) {                    break;                }            }        }        return video;    }}